sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading && !g_form.isNewRecord() || newValue === '' || newValue == oldValue) {
			return;
		}

	var getemailadd = new GlideAjax('GetEmailAddress');
	getemailadd.addParam('sysparm_name','getEmail');
	getemailadd.addParam('sysparm_userID', g_form.getValue('u_requested_for'));
	getemailadd.getXML(populateField);

	function populateField(response){
		var value = response.responseXML.documentElement.getAttribute("answer");
		g_form.clearValue("u_requested_for_email");
		g_form.setValue("u_requested_for_email",value);
	}

}
